import { useState,useEffect } from "react";
import feather from "feather-icons"

function Header(){
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    useEffect(() => {
        feather.replace();
    }, [isSidebarOpen]); // Run when isSidebarOpen changes

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    }

    return(
        <header className="navi">
            <div className="nav-container">
            <ul>
                <img src="/imgs/temple.png" alt="Logo" />
                <nav className="desktop-navi">
                    <div className="links">

                        <ul 
                            className="sidebar" 
                            style={{ display: isSidebarOpen ? 'flex' : 'none' }}
                        >
                            <li><a href="#">Home</a></li>
                            <li><a href="#">Über uns</a></li>
                            <li><a href="#">Wettbewerbe</a></li>
                            <li><a href="#">Trainingszeiten</a></li>
                        </ul>
                        <li><a href="#">Home</a></li>
                        <li><a href="#">Über uns</a></li>
                        <li><a href="#">Wettbewerbe</a></li>
                        <li><a href="#">Trainingszeiten</a></li>
                        <li>
                            <a 
                                href="#" 
                                onClick={(e) => {
                                    e.preventDefault();
                                    toggleSidebar();
                                }} 
                                rel="noreferrer"
                            >
                                <i data-feather={isSidebarOpen ? "x" : "menu"} className="menu-icon"></i>
                            </a>
                        </li>
                    </div>
                </nav>
            </ul>
            </div>
        </header>
    );
}

export default Header;
