import { useState, useEffect } from "react";
import feather from "feather-icons"

function Header(){
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    useEffect(() => {
        feather.replace();
    }, [isSidebarOpen]);

    return(
        <header className="navi">
            <div className="nav-container">
                <img src="/imgs/temple.png" alt="Logo" />
                <nav className="desktop-navi">
                    <div className="links">
                        <ul className="sidebar">
                            <li className={isSidebarOpen ? 'show' : ''}><a href="#">Home</a></li>
                            <li className={isSidebarOpen ? 'show' : ''}><a href="#">Über uns</a></li>
                            <li className={isSidebarOpen ? 'show' : ''}><a href="#">Wettbewerbe</a></li>
                            <li className={isSidebarOpen ? 'show' : ''}><a href="#">Trainingszeiten</a></li>
                        </ul>
                        <li><a href="#">Home</a></li>
                        <li><a href="#">Über uns</a></li>
                        <li><a href="#">Wettbewerbe</a></li>
                        <li><a href="#">Trainingszeiten</a></li>
                        <li>
                            <a 
                                href="#" 
                                onClick={(e) => {
                                    e.preventDefault();
                                    setIsSidebarOpen(!isSidebarOpen);
                                }} 
                                rel="noreferrer"
                            >
                                <i data-feather={isSidebarOpen ? "x" : "menu"} className="menu-icon"></i>
                            </a>
                        </li>
                    </div>
                </nav>
            </div>
        </header>
    );
}

export default Header;
